import React, {  useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import {  getTask,updateTask } from '../../actions/task'; 

const initialState = {

    title:'',
    description: ''
};

const TaskForm = ({
    task: { _id,  task, loading },
    getTask
  }) => {
    const [formData, setFormData] = useState(initialState);

    useEffect(() => {
      if(!task) getTask(window.location.pathname.split('/').pop());
        if (!loading && task) {
        const taskData = { ...initialState };
        for (const key in task) {
          if (key in taskData) taskData[key] = task[key];
        }
        setFormData(taskData);
      }
    }, [loading, getTask, task]);

    const {
        id,
        title,
        description
    } = formData;

    const onBack = (e)=>
    {
        setFormData(null);
    }

    const onChange = (e) =>
      setFormData({ ...formData, [e.target.name]: e.target.value });

    // const onSubmit = (e) => {
    //   e.preventDefault();
    //   updateTask(window.location.pathname.split('/').pop(),formData);
    // //   createProfile(formData, navigate, task ? true : false);
    // };

    return (
      <section className="container">
        <h1 className="large text-primary">
          {'Edit Your Task'}
        </h1>
        <p className="lead">
          <i className="fas fa-user" />
          {' Add some changes to your task'}
        </p>

        <form className="form" onSubmit={
            updateTask(window.location.pathname.split('/').pop(),formData)
            } >

        {/* <form className="form" onSubmit={onSubmit}> */}
          <div className="form-group">
            <input
              type="text"
              placeholder="Title"
              name="title"
              value={title}
              onChange={onChange}
            />
          </div>

          <div className="form-group">
            <input
              type="text"
              placeholder="description"
              name="description"
              value={description}
              onChange={onChange}
            />
          </div>

          <input type="submit" className="btn btn-primary my-1" />
          <Link onClick={(onBack)} className="btn btn-light my-1" to="/dashboard">
            Go Back
          </Link>
        </form>
      </section>
    );
  };

  TaskForm.propTypes = {
    // createTask: PropTypes.func.isRequired,
    getTask: PropTypes.func.isRequired,
    task: PropTypes.object.isRequired
  };

  const mapStateToProps = (state) => ({
    task: state.task
  });

  export default connect(mapStateToProps, { getTask })(
    TaskForm
  );



